var axios = require('axios');
var Spotify = require('node-spotify-api');
var spotify = new Spotify ({
    id: process.env.SPOTIFY_ID,
    secret: process.env.SPOTIFY_SECRET
})

export default {

    getPosts: () => {
        return spotify.search({ type: 'track', query: 'All the Small Things' })

    }
}
