  //   },
  //   {
  //     title: 'Music and Covid',
  //     description: 'titans and steelers will postpone their game on sunday opting to move it to another day in the not too distant future.'
  //   }
  // ]

  return (
    <Carousel interval={5000} navButtonsAlwaysVisible={true}>
      {
        sort.map((item, i) => <MediaCard key={i} item={item}/>)
      }
    </Carousel>
  )
}


export function MediaCard(props) {
  const classes = useStyles();
  const { item } = props;

  return (
    <Card style={{backgroundColor: 'white'}} className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image= {item.image}
          title="music"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {item.title}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {item.description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="secondary">
          Queue
        </Button>
        <Button size="small" color="primary">
          Read More
        </Button>
      </CardActions>
    </Card>
  );
}
